mapper.select:
SELECT messageId, senderAccountId, nickname, subject, send_date, read_date, receiverView FROM message as m JOIN accounts as a ON m.senderAccountId = a.accountId
AND subject like CONCAT('%', #{keyword}, '%')
AND object like CONCAT('%', #{keyword}, '%')
AND senderAccountId like CONCAT('%', #{keyword}, '%')
AND receiverAccountId = #{accountId}
ORDER BY send_date desc LIMIT #{startList}, #{listSize}

SELECT messageId, receiverAccountId, nickname, subject, send_date, read_date, receiverView FROM message as m JOIN accounts as a ON m.receiverAccountId = a.accountId
AND subject like CONCAT('%', #{keyword}, '%')
AND object like CONCAT('%', #{keyword}, '%')
AND senderAccountId like CONCAT('%', #{keyword}, '%')
AND senderAccountId = #{accountId}
ORDER BY send_date desc LIMIT #{startList}, #{listSize}
